mapper.select:
SELECT r.idx, r.accom_name, r.large_area_idx, a.large_area_name, (select max(price) from tbl_accom_room where accom_idx = r.idx) as max_price, (select min(price) from tbl_accom_room where accom_idx = r.idx) as min_price, (select ai.file_name from tbl_accom_images ai where r.idx = ai.idx) as file_name FROM tbl_accom r ,tbl_large_area a
r.large_area_idx = a.idx AND r.large_area_idx = #{largeAreaIdx}
ORDER BY r.idx asc


        SELECT idx
        FROM tbl_wishlist_accom
		WHERE member_idx=#{memberIdx} 
    		AND accom_idx=#{accomIdx}

mapper.delete:
DELETE 
FROM tbl_wishlist_accom
WHERE member_idx=#{memberIdx} 
	AND accom_idx=#{accomIdx}
